import React, { useState } from "react";

function AddTransactionForm({newTransaction}) {
  const [formData, setFormData] = useState({
    date:"",
    description:"",
    category:"",
    amount:""
  })

  function handleSubmit(event){
    event.preventDefault();
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "content-Type": "application/json"
      },
      body: JSON.stringify(formData)
    })
    .then(r => r.json())
    .then((data) => newTransaction(data))

  }

  function handleChange(event){
    // event.preventDefault();
    const key = event.target.name
    const value = event.target.value;
    setFormData({...formData, [key]: value})
    // console.log(formData)
  }

  return (
    <div>
      <form onSubmit={(handleSubmit)}>
        <div>
          <input onChange={ handleChange } type="date" name="date" />
          <input onChange={ handleChange } type="text" name="description" placeholder="Description" />
          <input onChange={ handleChange } type="text" name="category" placeholder="Category" />
          <input onChange={ handleChange } type="number" name="amount" placeholder="Amount" />
        </div>
        <button type="submit">
          Submit
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
